INSERT INTO `youzhan_article` (`aid`, `title`, `title_jl`, `keywords`, `weight`, `discuss_open`, `titlecorlor`, `author`, `tags`, `description`, `from`, `istop`, `addtime`, `updatetime`, `endtime`, `imgurl`, `isshouye`, `content`, `hits`, `typeid`, `status`, `webconfig_id`, `article_img`, `articledel`, `redirect`) VALUES
(37, '人居|木结构改变城市建筑生态，激发园林城市主义潜能', '', '', 0, 1, 'black', '', '', '人居|木结构改变城市建筑生态，激发园林城市主义潜能', '', 0, 1501742575, 1501742297, NULL, '/muwu_admin/Public/ueditor/php/upload/47531501742574.png', '', '<p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><span style="font-family:宋体;">导语：优化城市布局，建造园林城市不仅为人们营造了舒适健康的居住环境，也将推动周边地区功能和经济的发展。</span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><span style="font-family:宋体;"><img src="/muwu_admin/Public/ueditor/php/upload/40151501742563.png" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;height:457px;width:700px;" /></span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-family:宋体;font-size:18px;"> &nbsp; &nbsp;</span><span style="font-size:18px;"><span style="font-family:宋体;">近些年来，随着中国城市发展规模不断扩大，高度集中的资源和功能已经难以辐射到整个城市，中心城区也因为人口大量聚集而不堪重负，交通拥堵、环境污染等问题随之而来。中国社会科学院发布的《城市蓝皮书：中国城市发展报告No.8》中指出：中国“亚健康”城市已达九成以上。</span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><span style="font-family:宋体;"><img src="/muwu_admin/Public/ueditor/php/upload/46391501742564.png" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;height:443px;width:700px;" /></span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><span style="font-family:宋体;"><img src="/muwu_admin/Public/ueditor/php/upload/43831501742566.png" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;height:446px;width:700px;" /></span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-family:宋体;font-size:18px;"> &nbsp; &nbsp;</span><span style="font-size:18px;"><span style="font-family:宋体;">随着住建部评选出第一批“国家生态园林城市”，为改善城市坏境指明了方向，园林城市主义正渐渐兴起。拓展生活空间，营造生态健康的居住空间需要合理分配城区功能与资源，增加园林和绿地面积在城市中所占比重；也需要合理进行棚户区改造，拓展城市郊区功能，科学地对城乡结合部进行规划。</span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><span style="font-family:宋体;"><img src="/muwu_admin/Public/ueditor/php/upload/38181501742569.png" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;height:393px;width:700px;" /></span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><span style="font-family:宋体;"><img src="/muwu_admin/Public/ueditor/php/upload/27151501742570.png" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;height:437px;width:700px;" /></span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-family:宋体;font-size:18px;"> &nbsp; &nbsp;</span><span style="font-size:18px;"><span style="font-family:宋体;">建设生态园林城市以人、社会与自然的和谐为核心。随着城市建设规划政策的收紧，大兴土木的现象逐渐减少，木结构作为装配式建筑，受到国家政策的支持，对改变城市建筑的单一性、平衡城市建筑生态具有重要意义；作为绿色建筑，木结构的主体为可再生建材，低碳环保的搭建方式可以有效降低施工污染、净化城市环境；作为文化景观，木结构不仅继承传统建筑精髓，也具有现代风格和功能，可以自然的融入城市环境并提供公共服务。良好的城市布局不仅为人们提供舒适健康的居住环境，也将平衡城市功能和资源的分配，可谓一举两得。</span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><span style="font-family:宋体;"><img src="/muwu_admin/Public/ueditor/php/upload/35591501742572.png" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;height:394px;width:700px;" /></span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><span style="font-family:宋体;"><img src="/muwu_admin/Public/ueditor/php/upload/47531501742574.png" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;height:386px;width:700px;" /></span></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-family:宋体;font-size:18px;"> &nbsp; &nbsp;</span><span style="font-size:18px;"><span style="font-family:宋体;">随着新城市城郊一体化工作的需求，以木结构为代表的绿色建筑/装配式建筑将更多的被纳入城市功能规划中，在营造宜居环境的同时为人们提供便利服务，在推动环保健康理念发展的同时促进都市生态景观建设；联众将继续坚持技术创新，完善服务体系，以低碳环保的木屋产品助力城市可持续发展。</span></span></p><p><br /></p>', 1, 25, 0, 110, 'Public/ueditor/php/upload/40151501742563.png', 1, ''),
(38, '以木屋，创造生活的想象', '', '', 0, 1, 'black', '', '', '', '', 0, 1501742635, 1501742604, NULL, '/muwu_admin/Public/ueditor/php/upload/60941501747434.jpg', '', '<p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;">想象比真实更有力量！很多生活的乏味，只源于失去了想象的勇气。</span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp; &nbsp; 那些看起来很自由的生活，那些看起来很困难的事情，只要去做了就会发现，其实并没那么难；只要肯尝试就会发现，其实并没那么遥不可及。</span></p><p style="text-align:center"><span style="font-size:18px;"><img src="/muwu_admin/Public/ueditor/php/upload/61941501747420.png" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;float:none;width:500px;height:289px;" width="500" height="289" border="0" hspace="10" vspace="10" /></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp; &nbsp;</span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;">“家越远，心越近。”旅行的路上，你不是旅人，而是归客！远方的家，就是每个人记忆深处的家。——这就是“远方的家”存在的意义。</span><br /></p><p style="text-align:center"><span style="font-size:18px;"><img src="/muwu_admin/Public/ueditor/php/upload/53931501747423.png" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;float:none;width:500px;height:342px;" width="500" height="342" border="0" hspace="10" vspace="10" /></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp; &nbsp; </span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp;“远方的家”休闲农庄位于中国第一个国家森林公园——张家界，距张家界市区仅二十分钟车程，是一个典型的土家院落。从青山绿水到翘檐飞角，从木楼小院到土墙栏杆，从青石板路到乡村美食……</span><br /></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><img src="/muwu_admin/Public/ueditor/php/upload/42351501747424.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:500px;height:333px;" width="500" height="333" border="0" hspace="10" vspace="10" /></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><br /></p><p style="text-align:center"><br /></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp; &nbsp; </span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp;院子里到处是勾起记忆的旧事物，比如石磨、土灶等，土墙上挂满了各种农具，让人恍惚回到了曾经的土家村落。这里的建筑完全采用木质结构，墙面上木纹流转，与自然融为一体，阳台、小院、树荫、蝉叫、蝶飞......一切那么自然，皆见主人的用心。</span><br /></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><img src="/muwu_admin/Public/ueditor/php/upload/33311501747428.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:600px;height:337px;" width="600" height="337" border="0" hspace="10" vspace="10" /></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp; </span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp; 湘西的山水气韵透现出一种匪夷所思的险峻，一种震撼人心的野性张狂。正是这种大自然的天籁神气，陶冶出湘西人“纵死犹闻侠骨香”的侠胆义肠。爽朗的湘西妹子，质朴的土家大叔，以及侠骨仁心的农庄主人</span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><br /></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><img src="/muwu_admin/Public/ueditor/php/upload/1271501747430.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:500px;height:667px;" width="500" height="667" border="0" hspace="0" vspace="0" /></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp; &nbsp; </span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp;“远方的家”既是旅人的家，也是瞿姓族人的家，这里做饭的大叔、端茶的小妹多为瞿姓族人，山庄的主人自然也姓瞿，就连这山庄也是在瞿家裕旧址上建立的。在这里，你能看到三两个操着湘西方言，在树荫下话家常的土家族大姐，也能在日暮时分，听到响彻山涧的土家歌谣。恍惚间，心似回到了那个最淳静的时光里。</span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><img src="/muwu_admin/Public/ueditor/php/upload/26061501747432.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:500px;height:281px;" width="500" height="281" border="0" hspace="0" vspace="0" /></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp; &nbsp; </span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;">“为了更好的生活条件，多数村民要到外面打工。现在有了这个农庄，村民在“家”工作就可以了，会种地的管理农作物，会做饭的就去厨房，会手工制品的就去教游客，大学毕业的娃娃也可以回来管理农庄。我是瞿家裕的人，自然有责任守护好它的幸福”，山庄主人用实际行动创造着他对生活的想象。</span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><br /></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><img src="/muwu_admin/Public/ueditor/php/upload/881501747433.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:375px;height:500px;" width="375" height="500" border="0" hspace="0" vspace="0" /></span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp; &nbsp;</span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"> &nbsp; &nbsp; 日暮倾斜，树荫下一条土狗半咪双眼打着盹，藤椅上的旅人慵懒地翻着书页，厨房里淳朴的大叔正码着一片片腊肉，草地上孩童追着五色的蝴蝶奔跑，廊下土家族母女闲聊着生活的趣闻，山涧里回荡着一声声笑语。这里，实现了每个人对于生活的想象。</span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><span style="font-size:18px;"><img src="/muwu_admin/Public/ueditor/php/upload/60941501747434.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:600px;height:400px;" width="600" height="400" border="0" hspace="0" vspace="0" /></span></p><p><br /></p>', 1, 25, 0, 110, 'http://www.lzmuwu.com/UploadImage/edit/images/1(40).png', 1, ''),
(39, '慢生活丨木屋带来的减压方式', '', '', 0, 1, 'black', '', '', '随着社会飞速发展，我们正在进入一个“焦虑时代”，无论是生活还是工作，都处于快节奏', '', 0, 1501742677, 1501742655, NULL, '/muwu_admin/Public/ueditor/php/upload/25841501742668.jpg', '', '<p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:14px;">随着社会飞速发展，我们正在进入一个“焦虑时代”，无论是生活还是工作，都处于快节奏，高紧绷的状态，就连孩子，周末的时间都被补习挤得满满的。越来越快的生活节奏、越来越大的压力，对人们的身心健康已经造成的影响愈加明显。</span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><img src="/muwu_admin/Public/ueditor/php/upload/82201501742666.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:600px;height:442px;" /></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:14px;">越来越多的人意识到：只有拥有健康的生活状态才能享受更美好的人生，并开始积极调整生活、工作状态，为休养和思考留下时间。这种慢生活的状态并不是懒惰或者拖延时间，而是一种生活态度，是一种健康的心态，是对人生节奏的掌控。</span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><img src="/muwu_admin/Public/ueditor/php/upload/48941501742666.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:600px;height:399px;" /></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><img src="/muwu_admin/Public/ueditor/php/upload/65671501742667.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:600px;height:464px;" /></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:14px;">追求慢生活最重要的是要顺应自然。想象一个场景：在经历了长时间的束缚和压力后，驾车远离城市的喧嚣，落脚在树林边的小木屋，煮一杯茶，捧一本书，静坐一下午，思考人生的真谛和方向；然后看夕阳缓缓的拖着光芒隐匿于山后，呼吸着清新的空气，将身体内的负能量全部排空。</span></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><img src="/muwu_admin/Public/ueditor/php/upload/87321501742667.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:600px;height:450px;" /></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><img src="/muwu_admin/Public/ueditor/php/upload/76431501742668.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:600px;height:400px;" /></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;text-align:center;margin-top:0px;margin-bottom:0px;"><img src="/muwu_admin/Public/ueditor/php/upload/25841501742668.jpg" style="margin:0px;padding:0px;border:none;display:inline-block;vertical-align:middle;width:600px;height:398px;" /></p><p style="padding:0px;color:#6e6e6e;font-family:微软雅黑;font-size:12px;background-color:#ffffff;margin-top:0px;margin-bottom:0px;"><span style="font-size:14px;">人生是一场旅途，重要的不是终点，而是路上的风景。体味生活，回归自然，一起来木屋中享受安静闲适的时光吧。</span></p><p><br /></p>', 1, 25, 0, 110, 'Public/ueditor/php/upload/82201501742666.jpg', 1, '');
